 <script >
document.addEventListener("DOMContentLoaded", function () {
    document.getElementById("registerBtn").addEventListener("click", async function (e) {
        e.preventDefault();

        let username = document.getElementById("username").value;
        let email = document.getElementById("email").value;
        let password = document.getElementById("password").value;

        let data = {
            username: username,
            email: email,
            password: password
        };

        try {
            let response = await fetch("http://localhost:8080/api/register", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(data)
            });

            let result = await response.json();
            
            if (response.ok) {
                document.getElementById("message").innerHTML = `<span style="color: green; background-color:#181818">${result.message}</span>`;

                setTimeout(() => {
                    window.location.href = "/login"; // Redirect 
                }, 1000);
            } else {
                document.getElementById("message").innerHTML = `<span style="color: red; background-color:#181818;max-width: 100%;">${result.messages.username, result.messages.email}</span>`;
            }
        } catch (error) {
            console.error("Error:", error);
            document.getElementById("message").innerHTML = `<span style="color: red;">Error connecting to API</span>`;
        }
    });
});

 </script>

